
public class Squa {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		 int num=1,sqr;
		 
		   while(num <= 25)
		   {
		      if(num % 2 !=0)
			    {
				  sqr= num*num;
				  System.out.println("Square of  "+ num + "  is :"+ sqr);
				}
			     
				num++;
				
			} 

	}

}
